import React, { useState } from "react";
import { useHistory } from "react-router-dom";

const BookEdit = (props) => {
    const history = useHistory();
    const [formData, setFormData] = useState({
        name: "Book",
        category: "NOVEL",
        authorId: 0,
        availableCopies: 0
    });

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value.trim()
        });
    };

    const onFormSubmit = (e) => {
        e.preventDefault();
        const name = formData.name !== "" ? formData.name : props.book.name;
        const category = formData.category !== "" ? formData.category : props.book.category;
        const authorId = formData.authorId !== 0 ? formData.authorId : props.book.authorId;
        const availableCopies = formData.availableCopies !== 0 ? formData.availableCopies : props.book.availableCopies;

        props.onEditProduct(props.book.id, name, category, authorId, availableCopies);
        history.push("/books");
    };

    return (
        <div className="row mt-5">
            <div className="col-md-5">
                <form onSubmit={onFormSubmit}>
                    <div className="form-group">
                        <label htmlFor="name">Book name</label>
                        <input
                            type="text"
                            className="form-control"
                            id="name"
                            name="name"
                            required
                            placeholder={props.book.name}
                            onChange={handleChange}
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="category">Category</label>
                        <select
                            name="category"
                            className="form-control"
                            onChange={handleChange}
                            value={formData.category}
                        >
                            {props.categories.map((category) => {
                                    if (props.book.category !== undefined &&
                                        props.book.category === category)
                                        return <option selected={props.book.category} value={author.category}>{author.category}</option>
                                    else return <option value={author.category}>{author.category}</option>

                                }

                            )}
                        </select>
                    </div>
                    <div className="form-group">
                        <label htmlFor="quantity">Quantity</label>
                        <input
                            type="number"
                            className="form-control"
                            id="quantity"
                            name="availableCopies"
                            placeholder={props.book.quantity}
                            required
                            onChange={handleChange}
                        />
                    </div>
                    <div className="form-group">
                        <label>Author</label>
                        <select
                            name="authorId"
                            className="form-control"
                            onChange={handleChange}
                            defaultValue="0"
                        >
                            {props.authors.map((author) => {
                                if (props.book.author !== undefined &&
                                    props.book.author.id === author.id)
                                    return <option selected={props.book.author.id} value={author.id}>{author.name} {author.surname}</option>
                                else return <option value={author.id}>{author.name} {author.surname}</option>
                                })})
                                </select>
                                </div>
                    <button type="submit" className="btn btn-primary">Submit</button>
                </form>
            </div>
        </div>
    );
};

export default BookEdit;
