import './App.css';
import React, {Component} from "react";
import Books from "../Books/BookList/books";
import EshopService from "../../repository/eshopRepository";
import {BrowserRouter as Router, Redirect, Route, Switch} from 'react-router-dom'
import Countries from "../Countires/countries";
import Authors from "../Authors/authors";
import Header from "../Header/header";

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            books: [],
            countries: [],
            authors: []
        }
    }
    render() {
        return(
            <Router>
                <Header/>
                    <main>
                        <div className="container">
                            <Switch>
                                <Route path="/books" exact render={() => <Books books={this.state.books}
                                                                                        onDelete={this.deleteBook}/>} />
                                <Route path="/countries" exact render={() => <Countries countries={this.state.countries} />} />
                                <Route path="/authors" exact render={() => <Authors authors={this.state.authors} />} />
                                <Redirect to="/books" />
                            </Switch>
                        </div>
                    </main>
            </Router>
        );
    }

    loadBooks = () => {
        EshopService.fetchBooks()
            .then((data) => {
                this.setState({
                    books: data.data
                })
            })
    }
    loadAuthors = () => {
        EshopService.fetchAuthors()
            .then((data) => {
                this.setState({
                    authors: data.data
                })
            })
    }
    loadCountries = () => {
        EshopService.fetchCountries()
            .then((data) => {
                this.setState({
                    countries: data.data
                })
            })
    }

    deleteBook = (id) =>{
        EshopService.deleteBook(id)
            .then(() => {
                this.loadBooks();
            })
    }

    addBook = (name, category, authorId, availableCopies) =>{
        EshopService.addBook(name, category, authorId, availableCopies)
            .then(() => {
                this.loadBooks();
            })
    }
    componentDidMount() {
        this.loadBooks();
        this.loadAuthors();
        this.loadCountries();
    }
}
export default App;
